package Java.Exam.Questions.Jan242020;

// Q7: Write a Java program to print the ascii value of a given character.

public class Q7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char ch = 'A';
		
		int asciiCh;
		
		asciiCh = ch;
		
		System.out.println("ASCII value of character " + ch + " is :" + asciiCh);
		
		System.out.println("ASCII value of character using casting " + ch + " is : " + (int) ch);

	}

}
