package JavaBasics.Day2;

/*
 * 				Data types
 * 
 * Primitive   								non-primitive
 * 												-- String
 * 												-- Arrays
 * 												-- etc
 * -- Boolean
 * 		-- boolean
 * 
 * -- numeric
 * 	-- character
 * 		-- char
 *  -- Integral
 *  	-- Integer
 *  		-- byte
 *  		-- short
 *  		-- int
 *  		-- long
 *  	-- Float-Point
 *  		-- float
 *  		-- double
 * 
 */

public class DataTypes {
	
	boolean b;
	int a;
	float c;
	char d;
	String e;
	long f;
	double g;
	short h;
	byte i;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		DataTypes dt = new DataTypes();
		
		System.out.println("boolean : " + dt.b);
		System.out.println("int : " + dt.a);
		System.out.println("float : " + dt.c);
		System.out.println("char : " + dt.d);
		System.out.println("String : " + dt.e);
		System.out.println("long : " + dt.f);
		System.out.println("double : " + dt.g);
		System.out.println("short : " + dt.h);
		System.out.println("byte : " + dt.i);

	}

}
